import React from 'react';
import './styles.scss';
import { Checkbox } from '../../../../../components/Checkbox';
import { Radio } from '../../../../../components/Radio';
import { TextArea } from '../../../../../components/TextArea';

const questionTypeMap = {
    1: 'Multi',
    2: 'Single',
    3: 'Yes/No',
    4: 'Short',
    5: 'Long',
}

const MultiAnswerQuestion = ({ id, question, answerOptions, handleChange, questionNum, mandatory, value }) => {
    console.log(question);
    const handleClick = (ev) => {
        console.log(ev.target);
    }

    return (
        <div className='multi-answer-question-container' id={id}>
            <div className={`question-text ${mandatory}`}>{questionNum} {'. '}{question}</div>
            { answerOptions.map((option, index) => (
                <Checkbox
                    checked={value.includes(index)}
                    label={option}
                    id={`${id}-op-${index+1}`}
                    onClick={handleClick}
                />
            ))}
        </div>
    );
};

const SingleAnswerQuestion = ({ id, question, answerOptions, handleChange, questionNum, mandatory, value }) => {
    console.log(question);
    const onChange = (ev) => {
        handleChange(ev.target.name, questionNum);
    }

    return (
        <div className='single-answer-question-container' id={id}>
            <div className={`question-text ${mandatory}`}>{questionNum} {'. '}{question}</div>
            { answerOptions.map((option, index) => (
                <Radio 
                    checked={value==index}
                    id={`${id}-op-${index+1}`}
                    handleChange={onChange}
                    label={option}
                    value={index}
                    name={index}
                />
            ))}
        </div>
    );
};

const YesNoQuestion = ( { id , question, questionNum, mandatory, handleChange, value }) => {
    const onChange = (ev) => {
        handleChange(ev.target.name, questionNum);
    }
    return (
        <div className='yes-no-answer-question-container' id={id}>
            <div className={`question-text ${mandatory}`}>{questionNum} {'. '}{question}</div>
            <Radio
                checked={value==0}
                label='Yes'
                id={`${id}-op-yes`}
                name={0}
                // onClick={handleClick}
                handleChange={onChange}
            />
            <Radio
                checked={value==1}
                label='No'
                id={`${id}-op-$No`}
                name={1}
                handleChange={onChange}
                // onClick={handleClick}
            />
        </div>
    );
};

const ShortAnswerQuestion = ( { id, question, questionNum, mandatory, value, handleChange }) => {
    const onChange = (val) => {
        console.log('value, ', val, questionNum);
        handleChange(val, questionNum)
    }

    return (
        <div className='short-answer-question-container' id={id}>
            <div className={`question-text ${mandatory}`}>{questionNum} {'. '}{question}</div>
            <TextArea 
                value={value}
                handleChange={onChange}
            />
        </div>
    );
};

const LongAnswerQuestion = ( { id, question, questionNum, mandatory, handleChange, value }) => {
    const onChange = (val) => {
        console.log('value, ', val, questionNum);
        handleChange(val, questionNum)
    }

    return (
        <div className='long-answer-question-container' id={id}>
            <div className={`question-text ${mandatory}`}>{questionNum} {'. '}{question}</div>
            <TextArea 
                value={value}
                handleChange={onChange}
            />

        </div>
    );
};

export const Question = (props) => {
    const { question, questionNum, handleChange, value } = props;

    const handleClick = (ev) => {
        console.log(ev.target);
    }

    return (
        <div className='screening-question-container'>
            {questionTypeMap[question.type] === 'Multi' ? (
                <MultiAnswerQuestion 
                    id={question._id}
                    question={question.question}
                    answerOptions={question.answerOptions}
                    questionNum={questionNum}
                    mandatory={question.mandatory}
                    value={value}
                    handleClick={handleClick}
                />
            ) : 
            questionTypeMap[question.type] === 'Single' ? (
                <SingleAnswerQuestion
                    id={question._id}
                    question={question.question}
                    answerOptions={question.answerOptions}
                    questionNum={questionNum}
                    mandatory={question.mandatory}
                    value={value}
                    handleChange={handleChange}
                />
            )
            :
            questionTypeMap[question.type] === 'Yes/No' ? (
                <YesNoQuestion
                    id={question._id}
                    question={question.question}
                    questionNum={questionNum}
                    mandatory={question.mandatory}
                    value={value}
                    handleChange={handleChange}
                />
            )
            :
            questionTypeMap[question.type] === 'Short' ? (
                <ShortAnswerQuestion
                    id={question._id}
                    question={question.question}
                    questionNum={questionNum}
                    mandatory={question.mandatory}
                    value={value}
                    handleChange={handleChange}
                />
            )
            :
            questionTypeMap[question.type] === 'Long' ? (
                <LongAnswerQuestion
                    id={question._id}
                    question={question.question}
                    questionNum={questionNum}
                    mandatory={question.mandatory}
                    value={value}
                    handleChange={handleChange}
                />
            ):
            null
            }
            {question.mandatory && 
                <div className='validation-error'>
                    *Recruiter has marked this question Mandatory to answer
                </div>
            }
        </div>  
    );
}


