import React from 'react';
import './styles.scss';
import { Checkbox } from '../../../../../components/Checkbox';
import { Radio } from '../../../../../components/Radio';
import { TextArea } from '../../../../../components/TextArea';

const questionTypeMap = {
    1: 'Multi',
    2: 'Single',
    3: 'Yes/No',
    4: 'Short',
    5: 'Long',
}

const MultiAnswerQuestion = ({ id, question, answerOptions, handleClick, questionNum }) => {
    console.log(question);

    return (
        <div className='multi-answer-question-container' id={id}>
            <div className='question-text'>{questionNum} {' '}{question}</div>
            { answerOptions.map((option, index) => (
                <Checkbox
                    checked={false}
                    label={option}
                    id={`${id}-op-${index+1}`}
                    onClick={handleClick}
                />
            ))}
        </div>
    );
};

const SingleAnswerQuestion = ({ id, question, answerOptions, handleChange, questionNum }) => {
    console.log(question);

    return (
        <div className='single-answer-question-container' id={id}>
            <div className='question-text'>{questionNum} {' '}{question}</div>
            { answerOptions.map((option, index) => (
                <Radio 
                    checked={false}
                    id={`${id}-op-${index+1}`}
                    handleChange={handleChange}
                    label={option}
                />
            ))}
        </div>
    );
};

const YesNoQuestion = ( { id , question, questionNum}) => {
    return (
        <div className='yes-no-answer-question-container' id={id}>
            <div className='question-text'>{questionNum} {' '}{question}</div>
        </div>
    );
};

const ShortAnswerQuestion = ( { id, question, questionNum }) => {
    return (
        <div className='short-answer-question-container' id={id}>
        </div>
    );
};

const LongAnswerQuestion = ( { id, question, questionNum }) => {
    return (
        <div className='long-answer-question-container' id={id}>
            <div className='question-text'>{questionNum} {' '}{question}</div>
            <TextArea 
                value={2}
                handleChange={() => {}}
            />

        </div>
    );
};

export const Question = (props) => {
    const { question, questionNum } = props;
    console.log(question);
    return (
        <div className='screening-question-container'>
            {questionTypeMap[question.type] === 'Multi' ? (
                <MultiAnswerQuestion 
                    id={question._id}
                    question={question.question}
                    answerOptions={question.answerOptions}
                    questionNum={questionNum}
                />
            ) : 
            questionTypeMap[question.type] === 'Single' ? (
                <SingleAnswerQuestion
                    id={question._id}
                    question={question.question}
                    answerOptions={question.answerOptions}
                    questionNum={questionNum}

                />
            )
            :
            questionTypeMap[question.type] === 'Yes/No' ? (
                <YesNoQuestion
                    id={question._id}
                    question={question.question}
                    questionNum={questionNum}

                />
            )
            :
            questionTypeMap[question.type] === 'Short' ? (
                <ShortAnswerQuestion
                    id={question._id}
                    question={question.question}
                    questionNum={questionNum}

                />
            )
            :
            questionTypeMap[question.type] === 'Long' ? (
                <LongAnswerQuestion
                    id={question._id}
                    question={question.question}
                />
            ):
            null
            }
            {question.mandatory && 
                <div className='validation-error'>
                    *Recruiter has marked this question Mandatory to answer
                </div>
            }
        </div>  
    );
}
