import React from "react";
import { Radio } from "../../../../../components/Radio";

export const YesNoQuestion = ( { id , question, questionNum, mandatory, handleChange, value }) => {
    const onChange = (ev) => {
        handleChange(ev.target.name, questionNum);
    }
    return (
        <div className='yes-no-answer-question-container' id={id}>
            <div className={`question-text ${mandatory ? 'mandatory' : ''}`}>{questionNum} {'. '}{question}</div>
            <div className='answer-options'>
            <Radio
                checked={value===0}
                label='Yes'
                id={`${id}-op-yes`}
                name={0}
                // onClick={handleClick}
                handleChange={onChange}
            />
            <Radio
                checked={value===1}
                label='No'
                id={`${id}-op-$No`}
                name={1}
                handleChange={onChange}
                // onClick={handleClick}
            />
            </div>
        </div>
    );
};
