import React, { useEffect, useState } from 'react';
import './styles.scss';
import { getQuestionnaire } from '../../../../services/questionnaire/getQuestionnaire';
import { Question } from './components/Question';
import { Button } from '../../../components/Button';

export const ScreeningJobSeeker = (props) => {
    const { seekerId, associationPublished } = props;
    const INVOCATION = 1;

    const [questionnaire, setQuestionnaire] = useState({});
    const [questions, setQuestions] = useState([]);

    useEffect(() => {
        fetchQuestionaire();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);  


    const fetchQuestionaire = async () => {
        const params = {
            associationPublished: associationPublished,
            invocation: INVOCATION, 
            seeker: seekerId,
        }

        const resp = await getQuestionnaire(seekerId, params);
        const questionsData = resp.data.data[0].sections[0].questions;
        setQuestions(questionsData);
        setQuestionnaire(resp.data.data[0]);
    };

    const handleSubmit = () => {
        const questionnaireId = questionnaire._id;
        console.log(questionnaireId);
    }

    return (
        <div className='jobseeker-screening-container'>
            <div className='container-heading'>
                All mandatory Questions are marked with
            </div>         
            <div className='screening-questions-container'>
                {questions.map((question, index) => (
                    <Question key={question._id} question={question} questionNum={index+1} />
                ))}
            </div>
            <div className='submission-section'>
                <Button
                    onClick={handleSubmit}

                >
                    Submit Response
                </Button>
            </div>  
        </div>
    );
}
