import React from 'react';
import './styles.scss';
import { Checkbox } from '../../../../../components/Checkbox';
import { Radio } from '../../../../../components/Radio';

const questionTypeMap = {
    1: 'Multi',
    2: 'Single',
    3: 'Yes/No',
    4: 'Short',
    5: 'Long',
}

const MultiAnswerQuestion = ({ id, question, answerOptions, handleClick }) => {
    console.log(question);

    return (
        <div className='multi-answer-question-container' id={id}>
            {/* <div className='question-text'>{question}</div> */}
            { answerOptions.map((option, index) => (
                <Checkbox
                    checked={false}
                    label={option}
                    id={`${id}-op-${index+1}`}
                    onClick={handleClick}
                    label={question}
                />
            ))}
        </div>
    )
};

const SingleAnswerQuestion = ({ id, question, answerOptions, handleChange }) => {
    console.log(question);

    return (
        <div className='multi-answer-question-container' id={id}>
            <div className='question-text'>{question}</div>
            { answerOptions.map((option, index) => (
                <Radio 
                    checked={false}
                    id={`${id}-op-${index+1}`}
                    handleChange={handleChange}
                    label={question}
                />
            ))}
        </div>
    )
};

export const Question = (props) => {
    const { question } = props;
    console.log(question);
    return (
        <div className='screening-question-container'>
            {questionTypeMap[question.type] === 'Multi' ? (
                <MultiAnswerQuestion 
                    id={question._id}
                    question={question.question}
                    answerOptions={question.answerOptions}
                />
            ) : 

            null
            }
        </div>  
    );
}
