import React from 'react';
import './styles.scss';
import { Checkbox } from '../../../../../components/Checkbox';
import { Radio } from '../../../../../components/Radio';
import { TextArea } from '../../../../../components/TextArea';

const questionTypeMap = {
    1: 'Multi',
    2: 'Single',
    3: 'Yes/No',
    4: 'Short',
    5: 'Long',
}

const MultiAnswerQuestion = ({ id, question, answerOptions, handleClick, questionNum, mandatory }) => {
    console.log(question);

    return (
        <div className='multi-answer-question-container' id={id}>
            <div className={`question-text ${mandatory}`}>{questionNum} {'. '}{question}</div>
            { answerOptions.map((option, index) => (
                <Checkbox
                    checked={false}
                    label={option}
                    id={`${id}-op-${index+1}`}
                    onClick={handleClick}
                />
            ))}
        </div>
    );
};

const SingleAnswerQuestion = ({ id, question, answerOptions, handleChange, questionNum, mandatory }) => {
    console.log(question);

    return (
        <div className='single-answer-question-container' id={id}>
            <div className={`question-text ${mandatory}`}>{questionNum} {'. '}{question}</div>
            { answerOptions.map((option, index) => (
                <Radio 
                    checked={false}
                    id={`${id}-op-${index+1}`}
                    handleChange={handleChange}
                    label={option}
                />
            ))}
        </div>
    );
};

const YesNoQuestion = ( { id , question, questionNum, mandatory, handleClick }) => {
    return (
        <div className='yes-no-answer-question-container' id={id}>
            <div className={`question-text ${mandatory}`}>{questionNum} {'. '}{question}</div>
            <Checkbox
                checked={false}
                label='Yes'
                id={`${id}-op-yes`}
                onClick={handleClick}
            />
            <Checkbox
                checked={false}
                label='No'
                id={`${id}-op-$No`}
                onClick={handleClick}
            />
        </div>
    );
};

const ShortAnswerQuestion = ( { id, question, questionNum, mandatory }) => {
    return (
        <div className='short-answer-question-container' id={id}>
        </div>
    );
};

const LongAnswerQuestion = ( { id, question, questionNum, mandatory }) => {
    return (
        <div className='long-answer-question-container' id={id}>
            <div className={`question-text ${mandatory}`}>{questionNum} {'. '}{question}</div>
            <TextArea 
                value={2}
                handleChange={() => {}}
            />

        </div>
    );
};

export const Question = (props) => {
    const { question, questionNum } = props;
    console.log(question);
    return (
        <div className='screening-question-container'>
            {questionTypeMap[question.type] === 'Multi' ? (
                <MultiAnswerQuestion 
                    id={question._id}
                    question={question.question}
                    answerOptions={question.answerOptions}
                    questionNum={questionNum}
                    mandatory={question.mandatory}
                />
            ) : 
            questionTypeMap[question.type] === 'Single' ? (
                <SingleAnswerQuestion
                    id={question._id}
                    question={question.question}
                    answerOptions={question.answerOptions}
                    questionNum={questionNum}
                    mandatory={question.mandatory}
                />
            )
            :
            questionTypeMap[question.type] === 'Yes/No' ? (
                <YesNoQuestion
                    id={question._id}
                    question={question.question}
                    questionNum={questionNum}
                    mandatory={question.mandatory}
                />
            )
            :
            questionTypeMap[question.type] === 'Short' ? (
                <ShortAnswerQuestion
                    id={question._id}
                    question={question.question}
                    questionNum={questionNum}
                    mandatory={question.mandatory}
                />
            )
            :
            questionTypeMap[question.type] === 'Long' ? (
                <LongAnswerQuestion
                    id={question._id}
                    question={question.question}
                    questionNum={questionNum}
                    mandatory={question.mandatory}
                />
            ):
            null
            }
            {question.mandatory && 
                <div className='validation-error'>
                    *Recruiter has marked this question Mandatory to answer
                </div>
            }
        </div>  
    );
}
